import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { LoadingController } from '@ionic/angular';
import { FacebookLoginProvider, SocialAuthService } from 'angularx-social-login';
import { BlobService } from 'src/app/utils/blob.service';
import { CryptService } from 'src/app/utils/crypt.service';
import { CustomHttp } from 'src/app/utils/custom-http.service';
import { FbProcessService } from 'src/app/utils/fb-process.service';
import { ToastMessageService } from 'src/app/utils/toast-message.service';
import { UploadtostService } from 'src/app/utils/uploadtost.service';

declare var $:any;
@Component({
  selector: 'app-user-register',
  templateUrl: './user-register.component.html',
  styleUrls: ['./user-register.component.scss'],
})
export class UserRegisterComponent implements OnInit { 
  process_index = 0;
  process=[
    {
      isDone:false,
      text:"Privacy"
    },
    {
      isDone:false,
      text:"Account"
    },
    {
      isDone:false,
      text:"Basic"
    },
    {
      isDone:false,
      text:"Profile"
    },
    {
      isDone:false,
      text:"Contact"
    },
  ]

  professions=[
    'Human resources',
    'Shop owner',
    'Chat agent',
    'Facebook analyst' 
  ]

  account_form: FormGroup; 
  isNextProceed=false;
 get f_acc() { return this.account_form.controls; }
  basic_form: FormGroup; get f_basic() { return this.basic_form.controls; }
  profile_form: FormGroup; get f_profile() { return this.profile_form.controls; }
  contact_form: FormGroup; get f_contact() { return this.contact_form.controls; }

  acc_submitted=false;
  basic_submitted=false;
  profile_submitted=false;
  contact_submitted=false;

  check_privacy = false;
  check_contact = false;
  
  btn_nxt_status = true;
  btn_prev_status = true;
  provider = "email";
  mess_id = "";
  fb_access_token = "";

  profiles=["data:image/png;base64,",
  "data:image/png;base64,",
  "data:image/png;base64,",
  "data:image/png;base64,",
  "data:image/png;base64,",
  "data:image/png;base64,",]
  isImgFile = true;
  is_email_verified=false;
  constructor(
    private router:Router, 
    private cusHttp :CustomHttp,
    private authService:SocialAuthService,
    private toast:ToastMessageService,
    private loadingController:LoadingController,
    private route:ActivatedRoute,
    private formBuilder :FormBuilder) { 
    this.account_form = this.formBuilder.group({ 
      email: ['', [Validators.required,Validators.email]],
      password: ['', [Validators.required,Validators.minLength(7)]]
    }, { });
    this.basic_form = this.formBuilder.group({ 
      first_name: ['', [Validators.required]],
      last_name: ['', [Validators.required]],
      user_profile: [this.profiles[Math.floor(Math.random() * this.profiles.length)], [Validators.required]],
    }, { });
    this.profile_form = this.formBuilder.group({ 
      profession: ['', [Validators.required]] 
    }, { });
    this.contact_form = this.formBuilder.group({ 
      mobile_number: ['',[Validators.required,Validators.pattern("^[0-9]*$"),]]
    }, { });  
    route.url.subscribe((url:any) =>{
      if(!!router.url.includes('register/next-proceed')){
        this.isNextProceed = true;
        this.process_index = 3; 
        this.btn_nxt_status = false;
      }
    }); 
  }

  async ngOnInit() {   
  }

  imgPass = "eye";
  typePass="password";
  onClickShow(){ 
    this.typePass = this.typePass === 'text' ? 'password' : 'text';
    this.imgPass = this.imgPass === 'eye-off' ? 'eye' : 'eye-off';
  }
  
  getImage(){
    $("#baseFileUpload").click();
  }
  
  onChange(file) {
    if (file) {    
      this.isImgFile = true;
      BlobService.resize(file,440).then(async (data)=>{   
        const file = await BlobService.bitmapToBlob(data);  
        const uploaded_file = await UploadtostService.uploadFile(file,'user_profile/'+this.account_form.value.email); 
        this.basic_form.patchValue({user_profile:uploaded_file.Location}); 
      });
    }
  }
  async onNextAcc(){  
    this.acc_submitted = true;
    if(this.account_form.invalid){
      console.log("!onNextAcc");
      return false;
    }   
    this.provider = 'email';
    this.is_email_verified=false;
    const file = await BlobService.bitmapToBlob(this.profiles[Math.floor(Math.random() * this.profiles.length)]);  
    const uploaded_file = await UploadtostService.uploadFile(file,'user_profile/'+this.account_form.value.email); 
    this.basic_form.patchValue({user_profile:uploaded_file.Location});
    console.log(uploaded_file.Location);   
    return true;
  }  
  onNextBasic(){  
    this.basic_submitted = true;
    if(this.basic_form.invalid){
      console.log("!onNextBasic");
      return false;
    }   
    return true;
  }  
  onNextProfile(){  
    this.profile_submitted = true;
    if(this.profile_form.invalid){
      console.log("!onNextProfile");
      return false;
    }   
    return true;
  }  
  
  onNextContact(){  
    this.contact_submitted = true;
    if(this.contact_form.invalid || !this.check_contact){
      console.log("!onNextContact");
      return false;
    }   
    return true;
  }  

  onNext(){  
    switch(this.process_index){ 
      case 1:
        if(!this.onNextAcc()){ 
          return;
        } 
        break;
      case 2:
        if(!this.onNextBasic()){
          return;
        }
        break;
      case 3:
        if(!this.onNextProfile()){
          return;
        }
        break;
      case 4:
        if(!this.onNextContact()){
          return;
        }
        break; 
    }
    
    if(this.process_index <4){
      this.process_index++;  
      this.btn_prev_status = false; 
    }else{
      if(this.isNextProceed){
        this.isNextProceedDone();
      }else{ 
        this.onDone();
      }
    }
  }

  async isNextProceedDone() {
    const user_id = this.route.snapshot.paramMap.get('id');
    const data ={
      profession:this.profile_form.value.profession,
      mobile_number:this.contact_form.value.mobile_number,
    }
    var loading = await  this.loadingController.create({ message: "Please wait ...."  });
    await loading.present(); 

    this.cusHttp.put("user/fb-complete-register",data)
    .subscribe((snap)=>{
      loading.dismiss();
      console.log(JSON.stringify(snap));  
      this.toast.presentToast("Registered succesfully");
      this.router.navigateByUrl("p");
    },err=>{  
      loading.dismiss();
      console.log(err.error);
      this.toast.presentToast(err.error.error_message);
    });
  }

  async onDone() { 
    var loading = await  this.loadingController.create({ message: "Please wait ...."  });
    await loading.present(); 
    
    const data = {
      email:this.account_form.value.email,
      password:CryptService.encryptData(this.account_form.value.password),
      first_name:this.basic_form.value.first_name,
      last_name:this.basic_form.value.last_name,
      profession:this.profile_form.value.profession,
      mobile_number:this.contact_form.value.mobile_number,
      provider:this.provider,
      social_user_id :this.mess_id,
      user_img :this.basic_form.value.user_profile,
      fb_user_access_token:this.fb_access_token,
      is_email_verified: this.is_email_verified
    } 
    this.cusHttp.postNoToken("user/register",data)
    .subscribe((snap)=>{
      loading.dismiss();
      console.log(JSON.stringify(snap));  
      this.toast.presentToast("Registered succesfully");
      this.router.navigateByUrl("/auth/login");
    },err=>{  
      loading.dismiss();
      console.log(err.error);
      this.toast.presentToast(err.error.error_message);
    });
  }
 
  
  async signInWithFB()  {
    var loading = await  this.loadingController.create({ message: "Please wait ...."  });
    await loading.present(); 
    const fbLoginOptions = {
      // scope: 'pages_messaging,pages_messaging_subscriptions,email,pages_show_list,manage_pages',
      scope:'email,public_profile,pages_show_list,pages_messaging,pages_read_engagement,pages_manage_metadata',
      return_scopes: true,
      enable_profile_selector: true
     }; 
    this.authService.signIn(FacebookLoginProvider.PROVIDER_ID,fbLoginOptions ).then(async (snap)=>{
      console.log(snap);
      FbProcessService.stat_cusHttp = this.cusHttp; 
      const u_access_token = await FbProcessService.getLongLiveUserAToken(snap.authToken);
      const data = await FbProcessService.getLongLivePageToken(snap.id,u_access_token);
      await FbProcessService.prepPages(data);  
      // -----
      this.provider = 'facebook';
      this.mess_id = snap.id;
      this.account_form.patchValue({email:snap.email})
      this.basic_form.patchValue({first_name:snap.firstName,last_name:snap.lastName,user_profile:snap.photoUrl});
      this.fb_access_token = u_access_token;
      this.isImgFile = false; 
      this.is_email_verified=true;
      // -----

      this.process_index++;  
      this.btn_prev_status = false; 
      await loading.dismiss(); 
    }).catch(async (err)=>{
      await loading.dismiss(); 
      console.log(err);
    })
  }
  onBack(){
    console.log(this.process_index);
    if(this.process_index == 2){ 
      this.provider = 'email';
      this.mess_id = '';
      this.account_form.patchValue({email:'',password:''})
      this.basic_form.patchValue({first_name:'',last_name:'',user_profile:''});
      this.fb_access_token = '';
      this.isImgFile = true;
      this.is_email_verified=false;
    }
    
    if(this.isNextProceed && this.process_index == 3){
      this.btn_prev_status = true;
      return;
    }
    if(this.process_index > 0){ 
      this.process_index--; 
      console.log(this.process_index);
      this.btn_prev_status = false;
    }
    if(this.process_index == 0){ 
      this.btn_prev_status = true;
    }
    console.log(this.isNextProceed);
    console.log(this.process_index == 3);
  }
  
  onChangeCPrivacy(){
    console.log(this.check_privacy);
    this.btn_nxt_status = !this.check_privacy;
  }

  @Input() isConnectAcc :any;
  @Output() isPathLogin: EventEmitter<any> = new EventEmitter();
  haveAccount(){
    if(!this.isConnectAcc){
      this.router.navigateByUrl('/auth/login');
    }else{
      this.isPathLogin.emit();
    }
  } 
}
